#
# DO NOT EDIT THIS FILE DIRECTLY - UNLESS YOU KNOW WHAT YOU ARE DOING
#

user node[:yarn-bench][:user] do
  action :create
  supports :manage_home => true
  home "/home/#{node[:yarn-bench][:user]}"
  shell "/bin/bash"
  not_if "getent passwd #{node[:yarn-bench]['user']}"
end

group node[:yarn-bench][:group] do
  action :modify
  members ["#{node[:yarn-bench][:user]}"]
  append true
end


private_ip = my_private_ip()
public_ip = my_public_ip()



# Pre-Experiment Code
remote_file "/srv/hadoop/lib/native/libhopsndbevent.so" do
      source "http://snurran.sics.se/hops/libhopsndbevent.so"
      owner node[:hadoop][:yarn][:user]
      group node[:hadoop][:group]
      mode "0775"
      action :create_if_missing
end

